import type { Hadith } from '../types';

export const sahihBukhariHadiths: Hadith[] = [
  { id: 1, text: "عن عمر بن الخطاب رضي الله عنه قال: سمعت رسول الله صلى الله عليه وسلم يقول: (إنما الأعمال بالنيات، وإنما لكل امرئ ما نوى، فمن كانت هجرته إلى الله ورسوله، فهجرته إلى الله ورسوله، ومن كانت هجرته لدنيا يصيبها أو امرأة يتزوجها، فهجرته إلى ما هاجر إليه)", source: "رواه البخاري ومسلم" },
  { id: 2, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (من صلى علي صلاة واحدة صلى الله عليه عشراً)", source: "رواه مسلم" },
  { id: 3, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (من كذب علي متعمداً فليتبوأ مقعده من النار)", source: "رواه البخاري ومسلم" },
  { id: 4, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (كل أمتي يدخلون الجنة إلا من أبى) قالوا: يا رسول الله: ومن يأبى؟ قال: (من أطاعني دخل الجنة، ومن عصاني فقد أبى)", source: "رواه البخاري" },
  { id: 5, text: "عن أنس بن مالك الأنصاري رضي الله عنه أن النبي صلى الله عليه وسلم قال: (من رغب عن سنتي فليس مني)", source: "رواه البخاري ومسلم" },
  { id: 6, text: "عن أنس رضي الله عنه عن النبي صلى الله عليه وسلم أنه قال: (لا يؤمن أحدكم حتى أكون أحب إليه من والده وولده والناس أجمعين)", source: "رواه البخاري ومسلم" },
  { id: 7, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (قال الله تعالى: أنا أغنى الشركاء عن الشرك من عمل عملاً أشرك فيه معي غيري تركته وشركه)", source: "رواه مسلم" },
  { id: 8, text: "عن أبي هريرة رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (المؤمن القوي خير وأحب إلى الله من المؤمن الضعيف، وفي كل خير، احرص على ما ينفعك، واستعن بالله ولا تعجز، وإن أصابك شيء فلا تقل: لو أني فعلت كان كذا وكذا، ولكن قل: قدَر الله وما شاء فعل، فإن لو تفتح عمل الشيطان)", source: "رواه مسلم" },
  { id: 9, text: "عن أبي هريرة رضي الله عنه عن النبي صلى الله عليه وسلم قال: (الإيمان بضع وستون شعبة، والحياء شعبة من الإيمان)", source: "رواه البخاري ومسلم" },
  { id: 10, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (إن لله تسعة وتسعين اسما مائة إلا واحدا، من أحصاها دخل الجنة)", source: "رواه البخاري ومسلم" },
  { id: 11, text: "عن أبي هريرة رضي الله عنه عن النبي صلى الله عليه وسلم قال: قال الله عز وجل: (سبقت رحمتي غضبي)", source: "رواه البخاري ومسلم" },
  { id: 12, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (إن الله يغار وغيرة الله أن يأتي المؤمنُ ما حرم عليه)", source: "رواه البخاري ومسلم" },
  { id: 13, text: "عن أنس بن مالك رضي الله عنه عن النبي صلى الله عليه وسلم قال: (لا يؤمن أحدكم حتى يحب لأخيه ما يحب لنفسه)", source: "رواه البخاري ومسلم" },
  { id: 14, text: "عن عثمان بن عفان رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (خيركم من تعلم القرآن وعلمه)", source: "رواه البخاري" },
  { id: 15, text: "عن عمر بن الخطاب رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (إن الله يرفع بهذا الكتاب أقواما، ويضع به آخرين)", source: "رواه مسلم" },
  { id: 16, text: "عن أبي موسى الأشعري رضي الله عنه عن النبي صلى الله عليه وسلم قال: (تعاهدوا القرآن، فوالذي نفسي بيده لهو أشد تفصيِّا من الإبل في عُقُلها)", source: "رواه البخاري ومسلم" },
  { id: 17, text: "عن عبدالله بن عمرو رضي الله عنهما قال: قال رسول الله صلى الله عليه وسلم (اقرإ القرآن في شهر) قلت: إني أجد قوة حتى قال: (فاقرأه في سبع ولا تزد على ذلك)", source: "رواه البخاري ومسلم" },
  { id: 18, text: "عن عبدالله بن عمر رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (لا حسد إلا في اثنتين: رجل آتاه الله القرآن فهو يقوم به آناء الليل وآناء النهار، ورجل آتاه الله مالاً فهو ينفقه آناء الليل وآناء النهار)", source: "رواه البخاري ومسلم" },
  { id: 19, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (لا تجعلوا بيوتكم مقابر، إن الشيطان ينفر من البيت الذي تقرأ فيه سورة البقرة)", source: "رواه مسلم" },
  { id: 20, text: "عن أبي مسعود البدري رضي الله عنه قال: قال النبي صلى الله عليه وسلم: (من قرأ بالآيتين من آخر سور البقرة في ليلة كفتاه)", source: "رواه البخاري ومسلم" },
  { id: 21, text: "عن أبي الدرداء رضي الله عنه أن النبي صلى الله عليه وسلم قال: (من حفظ عشر آيات من أول سورة الكهف عُصم من الدجال)", source: "رواه مسلم" },
  { id: 22, text: "عن معاوية بن أبي سفيان رضي الله عنهما قال: سمعت النبي صلى الله عليه وسلم يقول: (من يرد الله به خيرا يفقهه في الدين)", source: "رواه البخاري ومسلم" },
  { id: 23, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (من سلك طريقاً يلتمس فيه علماً سهَّل الله له به طريقاً إلى الجنة)", source: "رواه مسلم" },
  { id: 24, text: "عن أبي هريرة رضي الله عنه عن النبي صلى الله عليه وسلم قال: (اجتنبوا السبع الموبقات)، قالوا: يا رسول الله وما هن؟ قال: (الشرك بالله، والسحر، وقتل النفس التي حرم الله إلا بالحق، وأكل الربا، وأكل مال اليتيم، والتولي يوم الزحف، وقذف المحصنات المؤمنات الغافلات)", source: "رواه البخاري ومسلم" },
  { id: 25, text: "عن ابن عمر رضي الله عنهما أن رسول الله صلى الله عليه وسلم قال: (كل مسكر خمر، وكل مسكر حرام)", source: "رواه مسلم" },
  { id: 26, text: "عن أبي بكرة رضي الله عنه قال: قال النبي صلى الله عليه وسلم: (أكبر الكبائر: الإشراك بالله، وعقوق الوالدين، وشهادة الزور)", source: "رواه البخاري ومسلم" },
  { id: 27, text: "عن عبدالله بن مسعود رضي الله عنه قال: قال النبي صلى الله عليه وسلم: (من مات وهو يدعو من دون الله ندا دخل النار)", source: "رواه البخاري" },
  { id: 28, text: "عن جابر بن عبدالله رضي الله عنهما قال: قال النبي صلى الله عليه وسلم: (من مات لا يشرك بالله شيئا دخل الجنة، ومن مات يشرك بالله شيئا دخل النار)", source: "رواه مسلم" },
  { id: 29, text: "عن علي بن أبي طالب رضي الله عنه قال: قال النبي صلى الله عليه وسلم: (لعن الله من ذبح لغير الله، ولعن الله من لعن والديه، ولعن الله من آوى محدثا، ولعن الله من غير منار الأرض)", source: "رواه مسلم" },
  { id: 30, text: "عن جابر رضي الله عنه قال: سمعت رسول الله صلى الله عليه وسلم يقول: (إن بين الرجل وبين الشرك والكفر ترك الصلاة)", source: "رواه مسلم" },
  { id: 31, text: "عن بريدة بن الحُصيب رضي الله عنه عن النبي صلى الله عليه وسلم قال: (من ترك صلاة العصر حَبِط عملُه)", source: "رواه البخاري" },
  { id: 32, text: "عن ابن عمر رضي الله عنهما أن رسول الله صلى الله عليه وسلم قال: (الذي تفوته صلاة العصر كأنما وُتِر أهلَه ومالَه)", source: "رواه البخاري ومسلم" },
  { id: 33, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (الصلوات الخمس، والجمعة إلى الجمعة، ورمضان إلى رمضان، مكفرات ما بينهن إذا اجتنبت الكبائر)", source: "رواه مسلم" },
  { id: 34, text: "عن عبدالله بن عمر بن الخطاب رضي الله عنهما عن النبي صلى الله عليه وسلم قال: (الظلم ظلمات يوم القيامة)", source: "رواه البخاري ومسلم" },
  { id: 35, text: "عن عائشة رضي الله عنها أن رسول الله صلى الله عليه وسلم قال: (من ظلم قِيد شبر من الأرض طُوِّقه من سبع أرَضِين)", source: "رواه البخاري ومسلم" },
  { id: 36, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (كل المسلم على المسلم حرام دمه وماله وعرضه)", source: "رواه مسلم" },
  { id: 37, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (أتدرون ما الغيبة؟) قالوا: الله ورسوله أعلم. قال: (ذكرك أخاك بما يكره) قيل: أرأيت إن كان في أخي ما أقول؟ قال: (إن كان فيه ما تقول فقد اغتبته، وإن لم يكن فيه ما تقول فقد بهته)", source: "رواه مسلم" },
  { id: 38, text: "عن عبدالله بن مسعود رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (أول ما يقضى بين الناس يوم القيامة في الدماء)", source: "رواه البخاري ومسلم" },
  { id: 39, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (من حمل علينا السلاح فليس منا، ومن غشنا فليس منا)", source: "رواه مسلم" },
  { id: 40, text: "عن ثابت بن الضحاك رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (لَعْنُ المؤمن كقتله)", source: "رواه البخاري ومسلم" },
  { id: 41, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (إياكم والظن، فإن الظن أكذب الحديث، ولا تحسسوا، ولا تجسسوا، ولا تنافسوا، ولا تحاسدوا، ولا تباغضوا، ولا تدابروا، وكونوا عباد الله إخوانا)", source: "رواه البخاري ومسلم" },
  { id: 42, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (إن العبد ليتكلم بالكلمة، ما يتبين ما فيها، يهوي بها في النار، أبعد ما بين المشرق والمغرب)", source: "رواه البخاري ومسلم" },
  { id: 43, text: "عن جابر رضي الله عنه قال: سمعت النبي صلى الله عليه وسلم يقول: (إن الشيطان قد أيس أن يعبده المصلون في جزيرة العرب، ولكن في التحريش بينهم)", source: "رواه مسلم" },
  { id: 44, text: "عن أبي هريرة رضي الله عنه عن النبي صلى الله عليه وسلم قال: (من لا يَرحم لا يُرحم)", source: "رواه البخاري ومسلم" },
  { id: 45, text: "عن خولة الأنصارية رضي الله عنها قالت: سمعت النبي صلى الله عليه وسلم يقول: (إن رجالا يتخوضون في مال الله بغير حق، فلهم النار يوم القيامة)", source: "رواه البخاري" },
  { id: 46, text: "عن ابن عمر رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (ما يزال الرجل يسأل حتى يأتي يوم القيامة وليس في وجهه مزعة لحم)", source: "رواه مسلم" },
  { id: 47, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (آية المنافق ثلاث: إذا حدث كذب، وإذا اؤتمن خان، وإذا وعد أخلف)", source: "رواه البخاري ومسلم" },
  { id: 48, text: "عن عبدالله بن مسعود رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (سباب المسلم فسوق، وقتاله كفر)", source: "رواه البخاري ومسلم" },
  { id: 49, text: "عن أبي سعيد الخدري رضي الله عنه قال: قال النبي صلى الله عليه وسلم: (لا تسبوا أصحابي، فلو أن أحدكم أنفق مثل أحد، ذهبا ما بلغ مد أحدهم، ولا نصيفه)", source: "رواه البخاري ومسلم" },
  { id: 50, text: "عن البراء بن عازب رضي الله عنهما قال: قال النبي صلى الله عليه وسلم: (الأنصار لا يحبهم إلا مؤمن، ولا يبغضهم إلا منافق، فمن أحبهم أحبه الله، ومن أبغضهم أبغضه الله)", source: "رواه البخاري ومسلم" },
  { id: 51, text: "عن علي بن أبي طالب رضي الله عنه قال: والذي فلق الحبة، وبرأ النَسَمة، إنه لعهد النبي صلى الله عليه وسلم إلي: (أن لا يحبني إلا مؤمن، ولا يبغضني إلا منافق)", source: "رواه مسلم" },
  { id: 52, text: "عن جُبير بن مطعم رضي الله عنه أنه سمع النبي صلى الله عليه وسلم يقول: (لا يدخلُ الجنةَ قاطعُ رحِم)", source: "رواه البخاري ومسلم" },
  { id: 53, text: "عن حذيفة بن اليمان رضي الله عنهما قال: سمعت رسول الله صلى الله عليه وسلم يقول: (لا يدخل الجنة نمام)", source: "رواه البخاري ومسلم" },
  { id: 54, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (لا يدخلُ الجنةَ من لا يأمنُ جارُه بوائقَه)", source: "رواه مسلم" },
  { id: 55, text: "عن ابن عمر رضي الله عنهما قال: قال رسول الله صلى الله عليه وسلم: (مازال جبريل يوصيني بالجار حتى ظننت أنه سيورثه)", source: "رواه البخاري ومسلم" },
  { id: 56, text: "عن جابر بن عبدالله الأنصاري رضي الله عنهما قال: \" لعن رسول الله صلى الله عليه وسلم آكل الربا، ومؤكله، وكاتبه، وشاهديه\"، وقال: (هم سواء)", source: "رواه مسلم" },
  { id: 57, text: "عن عبدالله بن عباس رضي الله عنهما قال: \"لعن رسول الله صلى الله عليه وسلم المتشبهين من الرجال بالنساء، والمتشبهات من النساء بالرجال\"", source: "رواه البخاري" },
  { id: 58, text: "عن عائشة وعبد الله بن عباس رضي الله عنهما قالا: قال رسول الله صلى الله عليه وسلم: (لعنة الله على اليهود والنصارى، اتخذوا قبور أنبيائهم مساجد)", source: "رواه البخاري ومسلم" },
  { id: 59, text: "عن عمر رضي الله عنه قال: سمعت النبي صلى الله عليه وسلم يقول: (لا تطروني، كما أطرت النصارى ابن مريم، فإنما أنا عبده، فقولوا عبدالله، ورسوله)", source: "رواه البخاري" },
  { id: 60, text: "عن عبدالله بن مسعود رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (أشد الناس عذابا يوم القيامة المصورون)", source: "رواه البخاري ومسلم" },
  { id: 61, text: "عن بعض أزواج النبي رضي الله عنهن عن النبي صلى الله عليه وسلم قال: (من أتى عرّافاً فسأله عن شيء لم تُقبل له صلاةٌ أربعين ليلة)", source: "رواه مسلم" },
  { id: 62, text: "عن أبي عامر أو أبي مالك الأشعري رضي الله عنه قال: سمعت النبي صلى الله عليه وسلم يقول: (ليكونن من أمتي أقوام يستحلون الحِرَ والحرير، والخمر والمعازف)", source: "رواه البخاري، والحِرَ الفرج، والمعنى أنهم يستحلون الزنا." },
  { id: 63, text: "عن أبي هريرة رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (كفى بالمرء كَذِبا أن يُحدث بكل ما سمع)", source: "رواه مسلم" },
  { id: 64, text: "عن عبدالله بن عمر بن الخطاب رضي الله عنهما عن النبي صلى الله عليه وسلم قال: (من جر ثوبه خُيلاء لم ينظرِ الله إليه يوم القيامة)", source: "رواه البخاري ومسلم" },
  { id: 65, text: "عن أبي سعيد الخدري رضي الله عنه قال: سمعت رسول الله صلى الله عليه وسلم يقول: (من رأى منكم منكرا فليغيره بيده، فإن لم يستطع فبلسانه، فإن لم يستطع فبقلبه، وذلك أضعف الإيمان)", source: "رواه مسلم" },
  { id: 66, text: "عن تميم بن أوس الداري رضي الله عنه أن النبي صلى الله عليه وسلم قال: (الدين النصيحة) قلنا: لمن؟ قال: (لله ولكتابه ولرسوله ولأئمة المسلمين وعامتهم)", source: "رواه مسلم" },
  { id: 67, text: "عن مَعْقِل بن يسار رضي الله عنه قال: سمعت رسول الله صلى الله عليه وسلم يقول: (ما من عبديسترعيه الله رعية، يموت يوم يموت وهو غاشٌ لرعيته، إلا حرم الله عليه الجنة)", source: "رواه البخاري ومسلم" },
  { id: 68, text: "عن أم سلمة رضي الله عنها أن رسول الله صلى الله عليه وسلم قال: (ستكون أمراء فتعرفون وتنكرون، فمن عرف برئ، ومن أنكر سلم، ولكن من رضي وتابع) قالوا: أفلا نقاتلهم؟ قال: (لا، ما صلوا)", source: "رواه مسلم" },
  { id: 69, text: "عن عبادة بن الصامت رضي الله عنه قال: بايعنا رسول الله صلى الله عليه وسلم على السمع والطاعة في منشطنا ومكرهنا، وعسرنا ويسرنا، وأثرة علينا، وأن لا ننازع الأمر أهله)، قال: (إلا أن تروا كفرا بواحا عندكم من الله فيه برهان)", source: "رواه البخاري ومسلم" },
  { id: 70, text: "عن أبي هريرة رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (إن الله لا ينظر إلى صوركم وأموالكم، ولكن ينظر إلى قلوبكم وأعمالكم)", source: "رواه مسلم" },
  { id: 71, text: "عن جرير بن عبدالله رضي الله عنه عن النبي صلى الله عليه وسلم قال: (أما إنكم سترون ربكم كما ترون هذا القمر، لا تضامون في رؤيته)", source: "رواه البخاري ومسلم" },
  { id: 72, text: "عن عبدالله بن عمرو بن العاص رضي الله عنهما قال: سمعت رسول الله صلى الله عليه وسلم يقول: (كتب الله مقادير الخلائق قبل أن يخلق السماوات والأرض بخمسين ألف سنة)", source: "رواه مسلم" },
  { id: 73, text: "عن أبي هريرة رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (من يُردِ الله به خيرا يُصِب منه)", source: "رواه البخاري" },
  { id: 74, text: "عن أبي هريرة وأبي سعيد رضي الله عنهما أن رسول الله صلى الله عليه وسلم قال: (ما يصيب المسلم من نصَب ولا وصَب ولا همٍّ ولا حزن ولا أذى ولا غمٍّ، حتى الشوكة يُشاكها إلا كفّر الله بها من خطاياه)", source: "رواه البخاري ومسلم" },
  { id: 75, text: "عن أبي هريرة رضي الله عنه عن النبي صلى الله عليه وسلم أنه قال: (لا يلدغ المؤمن من جُحر واحدٍ مرتين)", source: "رواه البخاري ومسلم" },
  { id: 76, text: "عن أم المؤمنين عائشة رضي الله عنها عن النبي صلى الله عليه وسلم قال: (إن الرفق لا يكون في شيء إلا زانه، ولا يُنزع من شيء إلا شانه)", source: "رواه مسلم" },
  { id: 77, text: "عن عائشة رضي الله عنها عن النبي صلى الله عليه وسلم قال: (إن الله رفيق يحب الرفق، ويعطي على الرفق ما لا يعطي على العنف، وما لا يعطي على ما سواه)", source: "رواه مسلم" },
  { id: 78, text: "عن أبي هريرة رضي الله عنه عن النبي صلى الله عليه وسلم قال: (إن الله تجاوز عن أمتي ما حدثت به أنفسها، ما لم تعمل أو تتكلم)", source: "رواه البخاري ومسلم" },
  { id: 79, text: "عن عمرو بن العاص رضي الله عنه أنه سمع رسول الله صلى الله عليه وسلم يقول: (إذا حكم الحاكم فاجتهد ثم أصاب فله أجران، وإذا حكم فاجتهد ثم أخطأ فله أجر)", source: "رواه البخاري ومسلم" },
  { id: 80, text: "عن أبي هريرة رضي الله عنه عن النبي صلى الله عليه وسلم قال: (إن الدين يسر، ولن يشاد الدين أحدٌ إلا غلبه، فسددوا وقاربوا، وأبشروا، واستعينوا بالغدوة والروحة وشيء من الدلجة)", source: "رواه البخاري" },
  { id: 81, text: "عن أنس رضي الله عنه عن النبي صلى الله عليه وسلم أنه قال: (يسروا ولا تعسروا، وبشروا ولا تنفروا)", source: "رواه البخاري ومسلم" },
  { id: 82, text: "عن عبدالله بن مسعود رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (هلك المتنطعون) قالها ثلاثا.", source: "رواه مسلم" },
  { id: 83, text: "عن أبي هريرة رضي الله عنه عن النبي صلى الله عليه وسلم قال: (دعوني ما تركتكم، إنما هلك من كان قبلكم بسؤالهم واختلافهم على أنبيائهم، فإذا نهيتكم عن شيء فاجتنبوه، وإذا أمرتكم بأمر فأتوا منه ما استطعتم)", source: "رواه البخاري ومسلم" },
  { id: 84, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (الدنيا سجن المؤمن وجنة الكافر)", source: "رواه مسلم" },
  { id: 85, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (حُجبت النار بالشهوات، وحجبت الجنة بالمكاره)", source: "رواه البخاري ومسلم" },
  { id: 86, text: "عن الأغر المزني رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (يا أيها الناس توبوا إلى الله، فإني أتوب في اليوم إليه مائة مرة)", source: "رواه مسلم" },
  { id: 87, text: "عن أبي هريرة رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (والذي نفسي بيده لو لم تذنبوا لذهب الله بكم، ولجاء بقوم يذنبون، فيستغفرون الله فيغفرُ لهم)", source: "رواه مسلم" },
  { id: 88, text: "عن شداد بن أوس رضي الله عنه عن النبي صلى الله عليه وسلم قال: (سيد الاستغفار أن تقول: اللهم أنت ربي لا إله إلا أنت، خلقتني وأنا عبدك، وأنا على عهدك ووعدك ما استطعت، أعوذ بك من شر ما صنعت، أبوء لك بنعمتك علي، وأبوء لك بذنبي، فاغفر لي، فإنه لا يغفر الذنوب إلا أنت)", source: "رواه البخاري" },
  { id: 89, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (لَلـَّهُ أشدُّ فرحاً بتوبة أحدكم من أحدكم بضالته إذا وجدها)", source: "رواه مسلم" },
  { id: 90, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (من تاب قبل أن تطلع الشمس من مغربها تاب الله عليه)", source: "رواه مسلم" },
  { id: 91, text: "عن النواس بن سمعان الأنصاري رضي الله عنه قال: سألت رسول الله صلى الله عليه وسلم عن البِر والإثم فقال: (البِر حسن الخُلُق، والإثم ما حاك في صدرك، وكرهت أن يطلع عليه الناس)", source: "رواه مسلم" },
  { id: 92, text: "عن أبي ذرّ الغِفاري رضي الله عنه قال: قال النبي صلى الله عليه وسلم: (لا تحقرن من المعروف شيئاً ولو أن تلقى أخاك بوجه طلق)", source: "رواه مسلم" },
  { id: 93, text: "عن أنس رضي الله عنه عن النبي صلى الله عليه وسلم أنه قال: (ما من مسلم يغرس غرساً أو يزرع زرعاً فيأكل منه طير أو إنسان أو بهيمة إلا كان له به صدقة)", source: "رواه البخاري ومسلم" },
  { id: 94, text: "عن أبي الدّرداء رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (ما من مسلم يدعو لأخيه بظهر الغيب إلا قال الملَك: ولك بمثل)", source: "رواه مسلم" },
  { id: 95, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال:( إن الله يقول يوم القيامة: أين المتحابون بجلالي اليوم أظلهم في ظلي يوم لا ظل إلا ظلي)", source: "رواه مسلم" },
  { id: 96, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (لا تدخلوا الجنة حتى تؤمنوا، ولا تؤمنوا حتى تحابوا، أولا أدلكم على شيء إذا فعلتموه تحاببتم؟ أفشوا السلام بينكم)", source: "رواه مسلم" },
  { id: 97, text: "عن ابن عمر رضي الله عنهما أن رسول الله صلى الله عليه وسلم قال:( المسلم من سلم المسلمون من لسانه ويده والمهاجر من هجر ما نهى الله عنه)", source: "رواه البخاري" },
  { id: 98, text: "عن ابن عمر رضي الله عنهما أن رسول الله صلى الله عليه وسلم قال: (المسلم أخو المسلم لا يظلمه ولا يسلمه، من كان في حاجة أخيه كان الله في حاجته، ومن فرّج عن مسلم كربة فرج الله عنه بها كربة من كرب يوم القيامة، ومن ستر مسلماً ستره الله يوم القيامة)", source: "رواه البخاري ومسلم" },
  { id: 99, text: "عن أبي موسى رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (المؤمن للمؤمن كالبنيان يشد بعضه بعضا)", source: "رواه البخاري ومسلم" },
  { id: 100, text: "عن النعمان بن بشير رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (ترى المؤمنين في تراحمهم وتوادهم وتعاطفهم، كمثل الجسد، إذا اشتكى عضوا تداعى له سائر جسده بالسهر والحمى)", source: "رواه البخاري ومسلم" },
  { id: 101, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (حق المسلم على المسلم خمس: ردُّ السلام وعيادة المريض، واتباع الجنائز، وإجابة الدعوة، وتشميت العاطس)", source: "رواه البخاري ومسلم" },
  { id: 102, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (الساعي على الأرملة والمسكين كالمجاهد في سبيل الله - وأحسبه قال: - وكالقائم الذي لا يفتر والصائم الذي لا يفطر)", source: "رواه البخاري ومسلم" },
  { id: 103, text: "عن أنس رضي الله عنه عن النبي صلى الله عليه وسلم أنه قال: (من أحب أن يُبسط له في رزقه، ويُنسأ له في أثره؛ فليصل رحمه)", source: "رواه البخاري ومسلم" },
  { id: 104, text: "عن ابن عمر رضي الله عنهما أن رسول الله صلى الله عليه وسلم قال: (أبرُّ البر أن يصل الرجلُ ودَّ أبيه)", source: "رواه مسلم" },
  { id: 105, text: "عن عبدالله بن عمرو رضي الله عنهما أن رسول الله صلى الله عليه وسلم قال: (الدنيا متاع، وخير متاع الدنيا المرأة الصالحة)", source: "رواه مسلم" },
  { id: 106, text: "عن أبي هريرة رضي الله عنه عن النبي صلى الله عليه وسلم قال: (تُنكح المرأة لأربع: لمالها، ولحسبها، ولجمالها، ولدينها، فاظفر بذات الدين تَرِبَت يداك)", source: "رواه البخاري ومسلم" },
  { id: 107, text: "عن أسامة بن زيد بن حارثة رضي الله عنهما قال: قال رسول الله صلى الله عليه وسلم: (ما تركت بعدي فتنةً هي أضر على الرجال من النساء)", source: "رواه البخاري ومسلم" },
  { id: 108, text: "عن ابن عباس رضي الله عنهما قال: قال النبي صلى الله عليه وسلم: (يحرم من الرضاع ما يحرم من النسب)", source: "رواه البخاري ومسلم" },
  { id: 109, text: "عن أبي هريرة رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (استوصوا بالنساء، فإن المرأة خلقت من ضلع، وإن أعوج شيء في الضلع أعلاه، فإن ذهبت تقيمه كسرته، وإن تركته لم يزل أعوج، فاستوصوا بالنساء)", source: "رواه البخاري ومسلم" },
  { id: 110, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (ليس الشديد بالصرعة، إنما الشديد الذي يملك نفسه عند الغضب)", source: "رواه البخاري ومسلم" },
  { id: 111, text: "عن عبدالله بن مسعود وأبي موسى الأشعري رضي الله عنهما قالا: قال رسول الله صلى الله عليه وسلم: (إن بين يدي الساعة أياما يرفع فيها العلم، وينزل فيها الجهل، ويكثر فيها الهرج) والهرج القتل", source: "رواه البخاري ومسلم" },
  { id: 112, text: "عن مَعْقِل بن يسار رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (العبادة في الهرج كهجرة إلي)", source: "رواه مسلم" },
  { id: 113, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (بادروا بالأعمال فتنا كقطع الليل المظلم، يصبح الرجل مؤمنا ويمسي كافرا، أو يمسي مؤمنا ويصبح كافرا، يبيع دينه بعرَضٍ من الدنيا)", source: "رواه مسلم" },
  { id: 114, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (من دعا إلى هدى كان له من الأجر مثل أجور من تبعه لا ينقص ذلك من أجورهم شيئاً ومن دعا إلى ضلالة كان عليه من الإثم مثل آثام من تبعه لا ينقص ذلك من آثامهم شيئاً)", source: "رواه مسلم" },
  { id: 115, text: "عن عائشة رضي الله عنها عن النبي صلى الله عليه وسلم قال: (من أحدث في أمرنا هذا ما ليس منه فهو رد)", source: "رواه البخاري ومسلم" },
  { id: 116, text: "عن أبي سعيد الخدري رضي الله عنه أن النبي صلى الله عليه وسلم قال: (لتتبعن سَنَن من قبلَكم شِبرا بشبر، وذراعا بذراع، حتى لو سلكوا جُحر ضبٍ لسلكتموه)، قلنا يا رسول الله: اليهود، والنصارى قال: (فمن؟!)", source: "رواه البخاري ومسلم" },
  { id: 117, text: "عن المستورد بن شداد رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (والله ما الدنيا في الآخرة إلا مثل ما يجعل أحدكم إصبعه في اليم، فلينظر بم تَرجِع؟)", source: "رواه مسلم" },
  { id: 118, text: "عن أبي هريرة رضي الله عنه قال:( ما عاب النبي صلى الله عليه وسلم طعاماً قطُّ، إن اشتهاه أكله وإن كرهه تركه)", source: "رواه البخاري ومسلم" },
  { id: 119, text: "عن أنس بن مالك رضي الله عنه قال: كنا عند عمر بن الخطاب فقال: \"نُهينا عن التكلف\"", source: "رواه البخاري" },
  { id: 120, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (ما نقصت صدقة من مال، وما زاد الله عبداً بعفوٍ إلا عزاً، وما تواضع أحد لله إلا رفعه الله)", source: "رواه مسلم" },
  { id: 121, text: "عن عائشة رضي الله عنها عن النبي صلى الله عليه وسلم قال: (من نذر أن يطيع الله فليطعه، ومن نذر أن يعصيه فلا يعصه)", source: "رواه البخاري" },
  { id: 122, text: "عن ابن عباس رضي الله عنهما قال: قال رسول الله صلى الله عليه وسلم: (نعمتان مغبون فيهما كثير من الناس: الصحة والفراغ)", source: "رواه البخاري" },
  { id: 123, text: "عن صهيب رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (عجبا لأمر المؤمن، إن أمره كله خير، وليس ذاك لأحد إلا للمؤمن، إن أصابته سراء شكر، فكان خيرا له، وإن أصابته ضراء، صبر فكان خيرا له)", source: "رواه مسلم" },
  { id: 124, text: "عن أنس رضي الله عنه عن النبي صلى الله عليه وسلم أنه قال: (إنما الصبر عند الصدمة الأولى)", source: "رواه البخاري ومسلم" },
  { id: 125, text: "عن أبي هريرة رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: قال الله (أعددت لعبادي الصالحين ما لا عين رأت، ولا أذن سمعت، ولا خطر على قلب بشر، فاقرءوا إن شئتم: {فلا تعلم نفسٌ ما أُخفي لهم من قُرة أعيُن})", source: "رواه البخاري ومسلم" },
  { id: 126, text: "عن عائشة رضي الله عنها عن النبي صلى الله عليه وسلم قال: (أحب العمل إلى الله ما داوم عليه صاحبه وإن قلّ)", source: "رواه البخاري ومسلم" },
  { id: 127, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (سبق المفردون) قالوا: وما المفردون يا رسول الله؟ قال: (الذاكرون الله كثيراً والذاكرات)", source: "رواه مسلم" },
  { id: 128, text: "عن أبي هريرة وأبي سعيد الخدري رضي الله عنهما عن النبي صلى الله عليه وسلم قال: (لا يقعد قوم يذكرون الله عز وجل إلا حفتهم الملائكة، وغشيتهم الرحمة، ونزلت عليهم السكينة، وذكرهم الله فيمن عنده)", source: "رواه مسلم" },
  { id: 129, text: "عن أبي موسى رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (مَثَلُ الذي يذكر ربه والذي لا يذكر ربه مَثَلُ الحي والميت)", source: "رواه البخاري ومسلم" },
  { id: 130, text: "عن أبي هريرة رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (كلمتان خفيفتان على اللسان، ثقيلتان في الميزان، حبيبتان إلى الرحمن، سبحان الله وبحمده، سبحان الله العظيم)", source: "رواه البخاري ومسلم" },
  { id: 131, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (من قال: سبحان الله وبحمده، في يوم مائة مرة، حطت خطاياه، وإن كانت مثل زبد البحر)", source: "رواه البخاري ومسلم" },
  { id: 132, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (لأن أقول: سبحان الله، والحمد لله، ولا إله إلا الله، والله أكبر أحب إلي مما طلعت عليه الشمس)", source: "رواه مسلم" },
  { id: 133, text: "عن أنس رضي الله عنه عن النبي صلى الله عليه وسلم أنه قال: (إن الله ليرضى عن العبد يأكل الأكلة فيحمده عليها، ويشرب الشربة فيحمده عليها)", source: "رواه مسلم" },
  { id: 134, text: "عن عائشة رضي الله عنها قالت: (كان النبي صلى الله عليه وسلم يذكر الله على كل أحيانه)", source: "رواه مسلم" },
  { id: 135, text: "عن أنس رضي الله عنه قال: كان النبي صلى الله عليه وسلم إذا دخل الخلاء قال: (اللهم إني أعوذ بك من الخُبْث والخبائث)", source: "رواه البخاري ومسلم" },
  { id: 136, text: "عن أبي هريرة رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (لا تُقبل صلاة أحدكم إذا أحدث حتى يتوضأ)", source: "رواه البخاري ومسلم" },
  { id: 137, text: "عن عثمان بن عفان رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (من توضأ فأحسن الوضوء خرجت خطاياه من جسده حتى تخرج من تحت أظفاره)", source: "رواه مسلم" },
  { id: 138, text: "عن أبي هريرة رضي الله عنه قال: سمعت النبي صلى الله عليه وسلم يقول: (إن أمتي يُدعون يوم القيامة غرا محجلين من آثار الوضوء)", source: "رواه البخاري ومسلم" },
  { id: 139, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (ويل للأعقاب من النار)", source: "رواه البخاري ومسلم" },
  { id: 140, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (إذا استيقظ أحدكم من نومه، فلا يغمس يده في الإناء حتى يغسلها ثلاثا، فإنه لا يدري أين باتت يده)", source: "رواه البخاري ومسلم" },
  { id: 141, text: "عن عائشة رضي الله عنها قالت:( كان النبي صلى الله عليه وسلم يحب التيمن ما استطاع في شأنه كله في طهوره وترجله وتنعله)", source: "رواه البخاري ومسلم" },
  { id: 142, text: "عن عبدالله بن عمر رضي الله عنهما عن رسول الله صلى الله عليه وسلم قال: (إذا أكل أحدكم فليأكل بيمينه، وإذا شرب فليشرب بيمينه، فإن الشيطان يأكل بشماله، ويشرب بشماله)", source: "رواه مسلم" },
  { id: 143, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (من توضأ فليستنثر، ومن استجمر فليوتر)", source: "رواه البخاري ومسلم" },
  { id: 144, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (لولا أن أشق على أمتي لأمرتهم بالسواك عند كل صلاة)", source: "رواه مسلم" },
  { id: 145, text: "عن أبي هريرة رضي الله عنه عن رسول الله صلى الله عليه وسلم أنه قال: (الفطرة خمس: الاختتان، والاستحداد، وقص الشارب، وتقليم الأظفار، ونتف الإبط)", source: "رواه البخاري ومسلم" },
  { id: 146, text: "عن عبدالله بن عمر رضي الله عنهما أن رسول الله صلى الله عليه وسلم قال: (خالفوا المشركين: وفِّروا اللحى، وأحفوا الشوارب)", source: "رواه البخاري ومسلم" },
  { id: 147, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال:(التثاؤب من الشيطان فإذا تثاءب أحدكم فليرده ما استطاع)", source: "رواه البخاري ومسلم" },
  { id: 148, text: "عن أبي هريرة رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (إذا وجد أحدكم في بطنه شيئا، فأشكل عليه أخرج منه شيء أم لا، فلا يخرجن من المسجد حتى يسمع صوتا، أو يجد ريحا)", source: "رواه مسلم" },
  { id: 149, text: "عن أبي سعيد الخدري رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (إذا سمعتم المؤذن فقولوا مثل ما يقول)", source: "رواه البخاري ومسلم" },
  { id: 150, text: "عن أنس بن مالك رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (من نسي صلاة فليصلها إذا ذكرها، لا كفارة لها إلا ذلك)", source: "رواه البخاري ومسلم" },
  { id: 151, text: "عن عبدالله بن مسعود رضي الله عنه قال: سألت النبي صلى الله عليه وسلم: أي العمل أحب إلى الله؟ قال: (الصلاة على وقتها)، قال: ثم أي؟ قال: (بر الوالدين) قال: ثم أي؟ قال: (الجهاد في سبيل الله)", source: "رواه البخاري ومسلم" },
  { id: 152, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (صلاة الجماعة أفضل من صلاة أحدكم وحده بخمسة وعشرين جزءا)", source: "رواه البخاري ومسلم" },
  { id: 153, text: "عن عثمان بن عفان رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (من صلى العشاء في جماعة فكأنما قام نصف الليل، ومن صلى الصبح في جماعة فكأنما قام الليل كله)", source: "رواه مسلم" },
  { id: 154, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (من غدا إلى المسجد أو راح أعد الله له في الجنة نُزُلاً كلما غدا أو راح)", source: "رواه البخاري ومسلم" },
  { id: 155, text: "عن أبي حُمَيد أو عن أبي أُسَيد الساعدي رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (إذا دخل أحدكم المسجد، فليقل: اللهم افتح لي أبواب رحمتك، وإذا خرج، فليقل: اللهم إني أسألك من فضلك)", source: "رواه مسلم" },
  { id: 156, text: "عن أبي قتادة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (إذا دخل أحدكم المسجد فليركع ركعتين قبل أن يجلس)", source: "رواه البخاري ومسلم" },
  { id: 157, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (ألا أدلكم على ما يمحو الله به الخطايا، ويرفع به الدرجات؟) قالوا بلى يا رسول الله قال: (إسباغ الوضوء على المكاره، وكثرة الخطا إلى المساجد، وانتظار الصلاة بعد الصلاة، فذلكم الرباط)", source: "رواه مسلم" },
  { id: 158, text: "عن عبادة بن الصامت رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (لا صلاة لمن لم يقرأ بفاتحة الكتاب)", source: "رواه البخاري ومسلم" },
  { id: 159, text: "عن أنس بن مالك رضي الله عنه عن النبي صلى الله عليه وسلم قال: (اعتدلوا في السجود، ولا يبسط أحدكم ذراعيه انبساط الكلب)", source: "رواه البخاري ومسلم" },
  { id: 160, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (أقرب ما يكون العبد من ربه وهو ساجد فأكثروا الدعاء)", source: "رواه مسلم" },
  { id: 161, text: "عن أبي هريرة رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (إذا فرغ أحدكم من التشهد الآخِر، فليتعوذ بالله من أربع: من عذاب جهنم، ومن عذاب القبر، ومن فتنة المحيا والممات، ومن شر المسيح الدجال)", source: "رواه البخاري ومسلم" },
  { id: 162, text: "عن ثوبان رضي الله عنه قال: كان رسول الله صلى الله عليه وسلمإذا انصرف من صلاته استغفر ثلاثا وقال: (اللهم أنت السلام ومنك السلام، تباركت ذا الجلال والإكرام)", source: "رواه البخاري ومسلم" },
  { id: 163, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (الملائكة تصلي على أحدكم ما دام في مصلاه الذي صلى فيه ما لم يُحدث، تقول: اللهم اغفر له، اللهم ارحمه)", source: "رواه البخاري ومسلم" },
  { id: 164, text: "عن عبدالله بن مغفل رضي الله عنه قال: قال النبي صلى الله عليه وسلم: (بين كل أذانين صلاة، بين كل أذانين صلاة)، ثم قال في الثالثة: (لمن شاء)", source: "رواه البخاري ومسلم" },
  { id: 165, text: "عن أم حبيبة أم المؤمنين رضي الله عنها قالت: سمعت رسول الله صلى الله عليه وسلم يقول: (ما من عبدمسلم يصلي لله كل يوم ثنتي عشرة ركعة تطوعا، غير فريضة، إلا بنى الله له بيتا في الجنة)", source: "رواه مسلم" },
  { id: 166, text: "عن عائشة رضي الله عنها قالت: (كان النبي صلى الله عليه وسلم لا يدعُ أربعاً قبل الظهر وركعتن قبل الغداة)", source: "رواه البخاري" },
  { id: 167, text: "عن عائشة رضي الله عنها عن النبي صلى الله عليه وسلم قال: (ركعتا الفجر خير من الدنيا وما فيها)", source: "رواه مسلم" },
  { id: 168, text: "عن أبي هريرة رضي الله عنه قال: أوصاني خليلي صلى الله عليه وسلم بثلاث: (صيام ثلاثة أيام من كل شهر، وركعتي الضحى، وأن أوتر قبل أن أنام)", source: "رواه البخاري ومسلم" },
  { id: 169, text: "عن ابن عمر رضي الله عنهما قال: قال رسول الله صلى الله عليه وسلم: (صلاة الليل مثنى مثنى، فإذا خشي أحدكم الصبح صلى ركعة واحدة توتر له ما قد صلى)", source: "رواه البخاري ومسلم" },
  { id: 170, text: "عن ابن عمر رضي الله عنهما أن رسول الله صلى الله عليه وسلم قال: (اجعلوا آخر صلاتكم بالليل وتراً)", source: "رواه البخاري ومسلم" },
  { id: 171, text: "عن أبي هريرة رضي الله عنه عن النبي صلى الله عليه وسلم قال: (إذا أقيمت الصلاة فلا صلاة إلا المكتوبة)", source: "رواه مسلم" },
  { id: 172, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال:(من أدرك ركعة من الصلاة فقد أدرك الصلاة)", source: "رواه البخاري ومسلم" },
  { id: 173, text: "عن أبي هريرة رضي الله عنه قال: سمعت رسول الله صلى الله عليه وسلم يقول: (إذا أقيمت الصلاة، فلا تأتوها تسعون، وأتوها تمشون، عليكم السكينة، فما أدركتم فصلوا، وما فاتكم فأتموا)", source: "رواه البخاري ومسلم" },
  { id: 174, text: "عن أنس بن مالك رضي الله عنه عن النبي صلى الله عليه وسلم قال: (سووا صفوفكم، فإن تسوية الصفوف من إقامة الصلاة)", source: "رواه البخاري ومسلم" },
  { id: 175, text: "عن أبي هريرة رضي الله عنه أن النبي صلى الله عليه وسلم قال: (خير يوم طلعت عليه الشمس يوم الجمعة، فيه خلق آدم، وفيه أدخل الجنة، وفيه أخرج منها، ولا تقوم الساعة إلا في يوم الجمعة)", source: "رواه مسلم" },
  { id: 176, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (إذا قلت لصاحبك: أنصت، يوم الجمعة والإمام يخطب، فقد لغوت)", source: "رواه البخاري ومسلم" },
  { id: 177, text: "عن أبي هريرة رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (من صام رمضان إيمانا واحتسابا، غفر له ما تقدم من ذنبه)", source: "رواه البخاري ومسلم" },
  { id: 178, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال:( أفضل الصيام بعد رمضان شهر الله المحرم، وأفضل الصلاة بعد الفريضة صلاة الليل)", source: "رواه مسلم" },
  { id: 179, text: "عن أبي قتادة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (ثلاث من كل شهر، ورمضان إلى رمضان، هذا صيام الدهر كله)", source: "رواه مسلم" },
  { id: 180, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (العمرة إلى العمرة كفارة لما بينهما، والحج المبرور ليس له جزاء إلا الجنة)", source: "رواه البخاري ومسلم" },
  { id: 181, text: "عن أبي هريرة رضي الله عنه قال: قال النبي صلى الله عليه وسلم: (من حج هذا البيت، فلم يرفث، ولم يفسق رجع كيوم ولدته أمه)", source: "رواه البخاري ومسلم" },
  { id: 182, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (لقنوا موتاكم: لا إله إلا الله)", source: "رواه مسلم" },
  { id: 183, text: "عن جابر رضي الله عنه قال: سمعت النبي صلى الله عليه وسلم قبل وفاته بثلاثٍ يقول: (لا يموتن أحدكم إلا وهو يُحسِن بالله الظن)", source: "رواه مسلم" },
  { id: 184, text: "عن جابر رضي الله عنه قال: سمعت رسول الله صلى الله عليه وسلم يقول: (يُبعث كل عبدعلى ما مات عليه)", source: "رواه مسلم" },
  { id: 185, text: "عن أبي هريرة رضي الله عنه عن النبي صلى الله عليه وسلم قال: (أسرعوا بالجنازة، فإن تك صالحة فخير تقدمونها، وإن يك سوى ذلك، فشر تضعونه عن رقابكم)", source: "رواه البخاري ومسلم" },
  { id: 186, text: "عن أنس رضي الله عنه عن النبي صلى الله عليه وسلم أنه قال: (يتبع الميت ثلاثة فيرجع اثنان ويبقى معه واحد، يتبعه أهله وماله وعمله، فيرجع أهله وماله، ويبقى عمله)", source: "رواه البخاري ومسلم" },
  { id: 187, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (من شهد الجنازة حتى يصلي عليها فله قيراط، ومن شهدها حتى تدفن فله قيراطان) قيل: وما القيراطان؟ قال: (مثل الجبلين العظيمين)", source: "رواه البخاري ومسلم" },
  { id: 188, text: "عن أبي هريرة رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (إذا مات الإنسان انقطع عمله إلا من ثلاث: صدقة جارية، أو علم ينتفع به، أو ولد صالح يدعو له)", source: "رواه مسلم" },
  { id: 189, text: "عن ابن عمر رضي الله عنهما أن رسول الله صلى الله عليه وسلم قال: (إن أحدكم إذا مات عُرض عليه مقعدُه بالغداة والعشي، إن كان من أهل الجنة فمن أهل الجنة، وإن كان من أهل النار فمن أهل النار، يقال: هذا مقعدك، حتى يبعثك الله إليه يوم القيامة)", source: "رواه البخاري ومسلم" },
  { id: 190, text: "عن عائشة رضي الله عنها قالت: قال النبي صلى الله عليه وسلم: (لا تسبوا الأموات، فإنهم قد أفضوا إلى ما قدموا)", source: "رواه البخاري" },
  { id: 191, text: "عن المغيرة بن شعبة رضي الله عنه عن النبي صلى الله عليه وسلم قال: (لا يزال طائفة من أمتي ظاهرين، حتى يأتيهم أمر الله وهم ظاهرون)", source: "رواه البخاري ومسلم" },
  { id: 192, text: "عن أبي هريرة رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (بدأ الإسلام غريبا، وسيعود كما بدأ غريبا، فطوبى للغرباء)", source: "رواه مسلم" },
  { id: 193, text: "عن أبي موسى الأشعري رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (من قاتل لتكون كلمةُ الله هي العليا، فهو في سبيل الله)", source: "رواه البخاري ومسلم" },
  { id: 194, text: "عن أبي هريرة رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (من مات ولم يغز، ولم يحدث به نفسه، مات على شُعبةٍ من نفاق)", source: "رواه مسلم" },
  { id: 195, text: "عن سهل بن حَنيف رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: (من سأل الله تعالى الشهادة بصدق بلَّغه الله منازل الشهداء وإن مات على فراشه)", source: "رواه مسلم" },
  { id: 196, text: "عن أبي هريرة رضي الله عنه عن رسول الله صلى الله عليه وسلم قال: (لا تقوم الساعة حتى تقاتلوا اليهود، حتى يقول الحجر وراءه اليهودي: يا مسلم، هذا يهودي ورائي فاقتله)", source: "رواه البخاري ومسلم" },
  { id: 197, text: "عن ابن عمر رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: (إن الغادر يُنصب له لواء يوم القيامة، فيقال: هذه غدرة فلان بن فلان)", source: "رواه البخاري ومسلم" },
  { id: 198, text: "عن عبدالله بن مسعود رضي الله عنه عن النبي صلى الله عليه وسلم أنه كان يقول: (اللهم إني أسألك الهدى والتقى، والعفاف والغنى)", source: "رواه مسلم" },
  { id: 199, text: "عن أبي هريرة رضي الله عنه قال: كان رسول الله صلى الله عليه وسلم يقول: (اللهم أصلح لي ديني الذي هو عصمة أمري، وأصلح لي دنياي التي فيها معاشي، وأصلح لي آخرتي التي فيها معادي، واجعل الحياة زيادة لي في كل خير، واجعل الموت راحة لي من كل شر)", source: "رواه مسلم" },
  { id: 200, text: "عن أنس رضي الله عنه قال: كان أكثر دعاء النبي صلى الله عليه وسلم: (اللهم ربنا آتنا في الدنيا حسنة وفي الآخرة حسنة وقنا عذاب النار)", source: "رواه البخاري ومسلم" }
];
